;?>/assets/img/transferencia.png" width="32" height="32">				</a>			</td>				<?php }?>			<?php if(isset($cita_transferida)){?>			<td>				<a href="<?php echo site_url();?>/cita/transferir/<?php echo $cita->id?>/<?php echo $cita_transferida;?>">					<img src="<?php echo base_url();?>/assets/img/transferencia.png" width="32" height="32">				</a>			</td>				<?php }?>		</tr>	<?php }}?>	</tbody></table>