<fieldset>	<legend>Cita</legend>	<script>		function actualizar(fechaTexto,ver_reservadas,empalme){			<?php foreach($doctores as $doctor){?>			$.post("<?php echo site_url();?>/cita/doctor_disponible/",{fecha:fechaTexto,doctor:<?php echo $doctor->id;?>,reservada:ver_reservadas,empalme:empalme},function(data){$("#th_<?php echo $doctor->id;?>").html(data);});			$.post("<?php echo site_url();?>/cita/horas_habiles_doctor_am/",{fecha:fechaTexto,doctor:<?php echo $doctor->id;?>,reservada:ver_reservadas,empalme:empalme},function(data){$("#select_hora_am_padre_<?php echo $doctor->id;?>").html(data);});			$.post("<?php echo site_url();?>/cita/horas_habiles_doctor_pm/",{fecha:fechaTexto,doctor:<?php echo $doctor->id;?>,reservada:ver_reservadas,empalme:empalme},function(data){$("#select_hora_pm_padre_<?php echo $doctor->id;?>").html(data);});			<?php }?>		}	</script><!--Esta funcion javasrcipt crea el calendario en base a las preferencias establecidas por el usuario-->	<script>	var articulos = new Array();	$.ajaxSetup({		async: false	});		$(function() {			$.getJSON("<?php echo site_url('/cita/dia_disponible?dia=');?>",function(data){				$.each(data, function(index, value){					articulos.push(value.dia);				});			});			$('#calendario').datepicker({				beforeShowDay: marcarDia,				onChangeMonthYear: getDias,				onSelect:function(dateText, inst){					$('#reservada').val(dateText);					$('#buton_mostrar_reservadas').val('Ver Hrs. Reservadas');					$('#buton_mostrar_reservadas').attr('disabled',false);					$('#buton_empalme').val('Habilitar Empalme');					$('#buton_empalme').attr('disabled',false);					$('#ver_reservadas').val('FALSE');					$('#empalme').val('FALSE');					actualizar(dateText,'FALSE','FALSE');				},				numberOfMonths: 2,				minDate: new Date()			})		});		function getDias(year,month){			dia = '';			if(year != undefined && month != undefined){				var aux_month = ((month+1)>13)?month+1:month;				aux_month = (aux_month<10)?'0'+aux_month:aux_month;				dia =  '01-' + aux_month + '-' + year;			}			articulos = [];			$.getJSON("<?php echo site_url('/cita/dia_disponible?dia=');?>"+dia,function(data){				$.each(data, function(index, value){					articulos.push(value.dia);				});			});		}		function marcarDia(date){			var dia = date.getDate();			var mes = date.getMonth();			mes = (mes+1<13)?mes+1:mes;			var anio = date.getFullYear();			var cadena = (dia<10)?'0'+dia:dia;			cadena += '-'; 			cadena += (mes<10)?'0'+mes:mes;			cadena += '-' + anio;						for(var i = 0; i < articulos.length; i++){				if(articulos[i]==cadena){					return [false, ''];				}			}			return [true, ''];		}	</script>	<div class="columnaizq">		<div id="calendario"></div>		<input id="buton_mostrar_reservadas" type="button" onclick="$('#ver_reservadas').val('TRUE');actualizar($('#reservada').val(),$('#ver_reservadas').val(),$('#empalme').val());$(this).val('Mostrando Hrs. Reservadas');$(this).attr('disabled',true);" value="Ver Hrs. Reservadas" />		<input id="buton_empalme" type="button" onclick="$('#empalme').val('TRUE');actualizar($('#reservada').val(),$('#ver_reservadas').val(),$('#empalme').val());$(this).val('Empalme Habilitado');$(this).attr('disabled',true);" value="Habilitar Empalme"/>		<input type="hidden" id="ver_reservadas" value="FALSE"/>		<input type="hidden" id="empalme" value="FALSE"/>	</div>	<div id="horario" class="columnaizq">		<?php foreach($doctores as $doctor){?>			<table style="display: inline">				<thead id="th_<?php echo $doctor->id;?>"></thead>				<tbody>					<tr><td>				<div id="select_hora_am_padre_<?php echo $doctor->id;?>" style="display:inline">				<!--<select size="16" style="font-size: 105%" id="select_hora_am_<?php echo $doctor->id;?>" name="fecha_hora" onclick="<?php foreach($doctores as $d){if($d->id!=$doctor->id){?>$('#select_hora_am_<?php echo $d->id;?>').val('');$('#select_hora_pm_<?php echo $d->id;?>').val('');<?php }}?>$('#select_hora_pm_<?php echo $doctor->id;?>').val('');$('#doctor').val(<?php echo $doctor->id;?>)"></select>-->				</div>				<div id="select_hora_pm_padre_<?php echo $doctor->id;?>" style="display:inline">				<!--<select size="16" style="font-size: 105%" id="select_hora_pm_<?php echo $doctor->id;?>" name="fecha_hora" onclick="<?php foreach($doctores as $d){if($d->id!=$doctor->id){?>$('#select_hora_am_<?php echo $d->id;?>').val('');$('#select_hora_pm_<?php echo $d->id;?>').val('');<?php }}?>$('#select_hora_am_<?php echo $doctor->id;?>').val('');$('#doctor').val(<?php echo $doctor->id;?>)"></select>-->				</div>					</td></tr>				</tbody>			</table>		<?php }?>		<input type="hidden" " id="doctor" name="doctor" />	</div>	<input type="hidden" id="reservada" /></fieldset>